const initialState = {
  loggedIn: false,
};

function authReducer(state = initialState, action) {
  switch (action.type) {
    case ADD_USER:
      return {
        ...state,
        auth: [...state.auth, action.payload],
      };
    case DELETE_USER:
      return {
        ...state,
        auth: state.auth.filter((auth) => auth.id !== action.payload),
      };
    case UPDATE_USER:
      return {
        ...state,
        auth: state.auth.map((auth) =>
          auth.id === action.payload.id ? action.payload : auth
        ),
      };
    default:
      return state;
  }
}

export default authReducer;
