const initialState = {
  user: "",
  loggedIn: false,
};

function authReducer(state = initialState, action) {
  switch (action.type) {
    case "LOGIN_SUCCESS":
      return {
        ...state,
        user: action.payload?._id,
        loggedIn: true,
      };
    case "SIGNUP_SUCCESS":
      return {
        ...state,
        user: action.payload?.id,
        loggedIn: true,
      };
    default:
      return state;
  }
}

export default authReducer;
