import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchTodos, deleteTodo, editTodo } from "../redux/actions/todoActions";

const TodoList = () => {
  const dispatch = useDispatch();
  const userId = useSelector((state) => state?.auth?._id || "");
  const todos = useSelector((state) => state?.todos);
  const loggedIn = useSelector((state) => state?.auth?.loggedIn || false);

  useEffect(() => {
    dispatch(fetchTodos(userId));
  }, [loggedIn, dispatch, userId]);

  const handleDelete = (todoId) => {
    dispatch(deleteTodo(todoId));
  };

  const handleEdit = (todoId) => {
  const todoToUpdate = todos.todos.find((todo) => todo._id === todoId);
  const updatedTitle = window.prompt("Enter updated title:", todoToUpdate.title);
  const updatedDescription = window.prompt("Enter updated description:", todoToUpdate.description);
  const updatedCompleted = window.confirm("Is the todo completed?");

  const updatedTodo = {
    title: updatedTitle || todoToUpdate.title,
    description: updatedDescription || todoToUpdate.description,
    completed: updatedCompleted,
  };

  dispatch(editTodo(todoId, updatedTodo));
};


  return loggedIn ? (
    <div>
      <h2>Todo List</h2>
      <ul>
        {todos.todos.map((todo) => (
          <li key={todo?._id}>
            <h3>{todo?.title}</h3>
            <p>{todo?.description}</p>
            <p>{todo?.completed ? "Completed" : "Incomplete"}</p>
            <button onClick={() => handleDelete(todo?._id)}>Delete</button>
            <button onClick={() => handleEdit(todo?._id)}>Edit</button>
          </li>
        ))}
      </ul>
    </div>
  ) : (
    <h2>LogIn First to see your TODOs</h2>
  );
};

export default TodoList;
